import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View,Pressable } from 'react-native';
import { NavigationContainer, TabActions } from '@react-navigation/native';
import { CreateBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from 'react-native-vector-icons/Ionicons'

import HomeScreen from './HomeScreen';
import UserBorrowedInventory from './UserBorrowedInventory';
//const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();
const homeName = "Home";
const uInvName = "Borrowed"
const settingsName = "Settings"
export default function App() {
 console.log("LOADED APP")
  return (
    <NavigationContainer>

      <Tab.Navigator
        initialRouteName={homeName}
        scteenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;
            let rn = route.name;
            if (rn === homeName) {
              iconName=focused? 'home': 'home-outlined'
            }
            else if (rn === uInvName) {
              iconName = focused? 'list' : 'list-outline'
            }
            else if (rn === settingsName) {
              iconName = focused? 'settings' : 'settings-outline'
            }
            return <Ionicons name={iconName} size={size} color = {color}/>
          }
        })}>
        <Tab.Screen name={homeName} component={HomeScreen} />
        <Tab.Screen name={uInvName} component={UserBorrowedInventory} />
        <Tab.Screen name={settingsName} component={UserBorrowedInventory} />


      </Tab.Navigator>

   {/* <Stack.Navigator>
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{ title: 'BorrowMii' }}
        />
        <Stack.Screen name="Borrowed" component={UserBorrowedInventory} />
      </Stack.Navigator> */}
    </NavigationContainer>

  );
}

const background = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'dodgerblue',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
const flexbox = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'orange',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 8,
    padding: 6
  },
});
const div = StyleSheet.create({
  container: {
    flex: .25,
    alignItems: 'center',
    justifyContent: 'center',
  },
});

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
  },
  text: {
    fontSize: 16
  },
  wrapperCustom: {
    borderRadius: 8,
    padding: 6
  },
  logBox: {
    padding: 20,
    margin: 10,
    borderWidth: StyleSheet.hairlineWidth,
    borderColor: '#f0f0f0',
    backgroundColor: '#f9f9f9'
  }
});
